mapper.select:
SELECT cma.ins_code, cma.identity_type, cma.mea_avatar_id
FROM cl_member cm
LEFT JOIN cl_member_avatar cma
ON cm.mem_idx = cma.mem_idx AND cma.ins_code = #{ins_code}
WHERE cm.mem_id = #{mem_id}
AND cm.delete_yn = "N"

SELECT cma.ins_code, cma.identity_type, cma.mea_avatar_id
FROM cl_member cm
INNER JOIN cl_member_avatar cma
ON cm.mem_idx = cma.mem_idx AND cma.ins_code = #{ins_code}
AND cma.identity_type = #{identity_type}
WHERE cm.mem_id = #{mem_id}
AND cm.delete_yn = "N"

mapper.update:
UPDATE cl_member_avatar cma 
INNER join cl_member cm 
ON cma.mem_idx = cm.mem_idx 
SET mea_avatar_id = #{mea_avatar_id}
WHERE cm.mem_id = #{mem_id}
AND cm.delete_yn = 'N'
AND cma.ins_code = #{ins_code}
AND cma.identity_type = #{identity_type}
